import { database } from "./firebase.js";
import { ref, onValue } from "https://www.gstatic.com/firebasejs/10.7.1/firebase-database.js";

document.addEventListener("DOMContentLoaded", () => {
  /* -----------------------------
   *  TOTAL Voltage & Current
   * ----------------------------- */
  const totalVoltageEl = document.getElementById("total-voltage-value");
  const totalCurrentEl = document.getElementById("total-current-value");
  const estimatedCostEl = document.getElementById("estimated-cost");

  const totalDataRef = ref(database, "totalData");
  onValue(totalDataRef, (snapshot) => {
    if (snapshot.exists()) {
      const data = snapshot.val();
      updateValue(totalVoltageEl, data.total_voltage, "V");
      updateValue(totalCurrentEl, data.total_current, "A");

      if (data.total_voltage && data.total_current) {
        updateEstimatedCost(data.total_voltage, data.total_current);
      }
    } else {
      totalVoltageEl.innerText = "No Data";
      totalCurrentEl.innerText = "No Data";
    }
  }, (error) => {
    console.error("Error reading totalData:", error);
  });

  /* -----------------------------
   *  SOCKET LISTENERS
   * ----------------------------- */
  const socket1Ref = ref(database, "socket1");
  setupSocketListeners(socket1Ref, {
    voltage: "socket1-voltage",
    current: "socket1-current",
    power: "socket1-power",
    cost: "socket1-cost",
  });

  const socket2Ref = ref(database, "socket2");
  setupSocketListeners(socket2Ref, {
    voltage: "socket2-voltage",
    current: "socket2-current",
    power: "socket2-power",
    cost: "socket2-cost",
  });

  const socket3Ref = ref(database, "socket3");
  setupSocketListeners(socket3Ref, {
    voltage: "socket3-voltage",
    current: "socket3-current",
    power: "socket3-power",
    cost: "socket3-cost",
  });

  /* -----------------------------
   *  Download Invoice
   * ----------------------------- */
  if (typeof window.jspdf !== "undefined") {
    const { jsPDF } = window.jspdf;
    const downloadBtn = document.getElementById("download-invoice-btn");

    if (downloadBtn) {
      downloadBtn.addEventListener("click", () => {
        // Fetch values from the webpage using the correct element IDs
        const voltageText = document.getElementById("total-voltage-value")?.textContent || "N/A";
        const currentText = document.getElementById("total-current-value")?.textContent || "N/A";
        const estimatedCostText = document.getElementById("estimated-cost")?.textContent || "N/A";

        // Generate Invoice Number & Date
        const now = new Date();
        const date = now.toLocaleDateString();
        const time = now.toLocaleTimeString();
        const invoiceNumber = Math.floor(Math.random() * 1000000); // Random Invoice Number

        // Initialize jsPDF
        const doc = new jsPDF();

        // Header and Title
        doc.setFont("helvetica", "bold");
        doc.setFontSize(24);
        doc.setTextColor(0, 102, 204); // Blue header color
        doc.text("SMARTWATTS INVOICE", 105, 20, { align: "center" });

        // Add Company Logo using Base64 (replace the placeholder with your actual string)
        const base64Logo = "data:image/png;base64,"; 
        try {
          doc.addImage(base64Logo, "PNG", 10, 10, 30, 30);
        } catch (e) {
          console.warn("Logo image could not be added. Check your Base64 string.", e);
        }

        // Invoice metadata
        doc.setFontSize(14);
        doc.setTextColor(0, 0, 0);
        doc.setFont("helvetica", "normal");
        doc.text(`Invoice #: ${invoiceNumber}`, 150, 30);
        doc.text(`Date: ${date}`, 20, 50);
        doc.text(`Time: ${time}`, 20, 60);

        // Overall Usage Table
        if (doc.autoTable) {
          doc.autoTable({
            startY: 70,
            head: [["Description", "Value"]],
            body: [
              ["Total Voltage", `${voltageText} V`],
              ["Total Current", `${currentText} A`],
              ["Estimated Monthly Cost", ` ${estimatedCostText}`]
            ],
            theme: "grid",
            styles: { fontSize: 12, halign: "center" },
            headStyles: { fillColor: [0, 102, 204] }
          });
        } else {
          console.error("jsPDF autoTable plugin is not loaded!");
        }

        // Detailed Socket Breakdown (Sample Data)
        // In a real scenario, fetch these values from your Firebase "socketX" snapshots.
        const socketUsageData = [
          {
            socket: "Socket 1",
            morning: "0 kWh",
            night: "0 kWh",
            weekly: "0 kWh",
            monthly: "0 kWh",
            cost: " 0"
          },
          {
            socket: "Socket 2",
            morning: "0 kWh",
            night: "0 kWh",
            weekly: "0 kWh",
            monthly: "0 kWh",
            cost: " 0"
          },
          {
            socket: "Socket 3",
            morning: "0 kWh",
            night: "0 kWh",
            weekly: "0 kWh",
            monthly: "0 kWh",
            cost: " 0"
          }
        ];

        // Prepare table data for socket breakdown
        const socketTableBody = socketUsageData.map(item => [
          item.socket,
          item.morning,
          item.night,
          item.weekly,
          item.monthly,
          item.cost
        ]);

        // Add Socket Breakdown Table
        const finalY = doc.lastAutoTable ? doc.lastAutoTable.finalY + 10 : 90;
        doc.autoTable({
          startY: finalY,
          head: [["Socket", "Morning Usage", "Night Usage", "Weekly Usage", "Monthly Usage", "Cost"]],
          body: socketTableBody,
          theme: "grid",
          styles: { fontSize: 12, halign: "center" },
          headStyles: { fillColor: [0, 102, 204] }
        });

        // Signature and Disclaimer
        const finalY2 = doc.lastAutoTable.finalY + 15;
        doc.text("Authorized Signature:", 20, finalY2);
        doc.line(20, finalY2 + 5, 80, finalY2 + 5);
        doc.setFontSize(10);
        doc.setTextColor(100, 100, 100);
        doc.text("Note: Billing is based on average Adani electricity tariffs in India. Actual costs may vary.", 20, finalY2 + 20);

        // Save the PDF
        doc.save(`SmartWatts_Invoice_${invoiceNumber}.pdf`);
        console.log("Detailed invoice downloaded.");
      });
    } else {
      console.error("Download button not found!");
    }
  } else {
    console.error("jsPDF is not loaded!");
  }

  /* -----------------------------
   *  Helper Functions
   * ----------------------------- */
  function updateValue(element, val, unit = "") {
    if (element && val !== null && val !== undefined && !isNaN(val)) {
      element.innerText = `${parseFloat(val).toFixed(2)} ${unit}`;
    } else if (element) {
      element.innerText = "No Data";
    }
  }

  function updateCost(element, val) {
    if (element && val !== null && val !== undefined && !isNaN(val)) {
      element.innerText = ` ${parseFloat(val).toFixed(2)}`;
    } else if (element) {
      element.innerText = "No Data";
    }
  }

  function setupSocketListeners(refPath, ids) {
    const voltageEl = document.getElementById(ids.voltage);
    const currentEl = document.getElementById(ids.current);
    const powerEl = document.getElementById(ids.power);
    const costEl = document.getElementById(ids.cost);

    onValue(refPath, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        updateValue(voltageEl, data.voltage, "V");
        updateValue(currentEl, data.current, "A");
        updateValue(powerEl, data.power, "W");
        updateCost(costEl, data.cost);
      } else {
        voltageEl.innerText = "No Data";
        currentEl.innerText = "No Data";
        powerEl.innerText = "No Data";
        costEl.innerText = "No Data";
      }
    });
  }

  function updateEstimatedCost(voltage, current) {
    let power = voltage * current; // in Watts
    let energy = (power * 30 * 24) / 1000; // Monthly energy in kWh
    let tariff = 5.5; // Rs/kWh (average tariff)
    let estimatedCost = energy * tariff;
    if (estimatedCostEl) {
      estimatedCostEl.textContent = estimatedCost.toFixed(2);
    }
  }
});

  // Line chart: Day vs Night consumption
  const ctxLine = document.getElementById('weeklyEnergyChart').getContext('2d');
  new Chart(ctxLine, {
    type: 'line',
    data: {
      labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
      datasets: [
        {
          label: 'Day (kWh)',
          data: [4, 5, 6, 5.5, 6.2, 7, 6.5],
          borderColor: '#3b82f6',
          backgroundColor: 'rgba(59, 130, 246, 0.1)',
          tension: 0.4,
          fill: true
        },
        {
          label: 'Night (kWh)',
          data: [3, 3.5, 4, 3.8, 4.1, 5, 4.3],
          borderColor: '#facc15',
          backgroundColor: 'rgba(250, 204, 21, 0.1)',
          tension: 0.4,
          fill: true
        }
      ]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: 'Energy (kWh)'
          }
        },
        x: {
          title: {
            display: true,
            text: 'Days'
          }
        }
      },
      plugins: {
        legend: {
          position: 'top'
        }
      }
    }
  });

  // Pie chart (your existing chart)
  const ctxPie = document.getElementById('appliancePieChart').getContext('2d');
  new Chart(ctxPie, {
    type: 'pie',
    data: {
      labels: ['AC', 'Lights', 'Fan'],
      datasets: [{
        data: [40, 30, 30],
        backgroundColor: ['#f87171', '#3b82f6', '#facc15'],
        hoverOffset: 10
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false
    }
  });